graphql.query:
query cmsPageTranslations {
    cmsPageTranslations(input: {
        cmsPageId: 1
    }) {
        paginatorInfo {
            count
            currentPage
            lastPage
            total
        }
        data {
            id
            urlKey
            metaDescription
            metaTitle
            pageTitle
            metaKeywords
            htmlContent
            locale
            cmsPageId
        }
    }
}

query cmsPageTranslation {
    cmsPageTranslation(
        id: 1,
        input: {
            cmsPageId: 1
        }
    ) {
        id
        urlKey
        metaDescription
        metaTitle
        pageTitle
        metaKeywords
        htmlContent
        locale
        cmsPageId
    }
}
