graphql.query:
query catalogRuleProductPrices {
    catalogRuleProductPrices(input: {
        catalogRuleId: 1,
        productId: 1,
        customerGroupId: 1,
        channelId: 1
    }) {
        paginatorInfo {
            count
            currentPage
            lastPage
            total
        }
        data {
            id
            price
            ruleDate
            startsFrom
            endsTill
            productId
            catalogRuleId
            channelId
            customerGroupId
        }
    }
}

query catalogRuleProductPrice {
    catalogRuleProductPrice(
        id: 1,
        input: {
            catalogRuleId: 1,
            productId: 1,
            customerGroupId: 1,
            channelId: 1
        }
    ) {
        id
        price
        ruleDate
        startsFrom
        endsTill
        productId
        catalogRuleId
        channelId
        customerGroupId
    }
}
