graphql.query:
query shipmentItems {
    shipmentItems(input: {
        shipmentId: 1
        # id: 1
        # orderItemId: 1
    }) {
        paginatorInfo {
            count
            currentPage
            lastPage
            total
        }
        data {
            id
            name
            description
            sku
            qty
            weight
            price
            basePrice
            total
            baseTotal
            productId
            productType
            orderItemId
            shipmentId
            additional
            createdAt
            updatedAt
        }
    }
}
