mutation paymentMethods {
    paymentMethods(input: {
        shippingMethod: "flatrate_flatrate"
    }) {
        success
        paymentMethods {
            method
            method_title
            description
            sort
        }
        cart {
            id
            customerEmail
            customerFirstName
            customerLastName
            shippingMethod
            couponCode
            isGift
            itemsCount
            itemsQty
        }
    }
}

mutation placeOrder {
    placeOrder {
        success
        redirectUrl
        selectedMethod
        order {
            id
            customerEmail
            customerFirstName
            customerLastName
        }
    }
}

query shippingMethods {
    shippingMethods {
        success
        shippingMethods {
            title
            methods {
                code
                label
                price
                formattedPrice
                basePrice
                formattedBasePrice                                          
            }
        }
        paymentMethods {
            method
            method_title
            description
            sort
        }
        cart {
            id
            customerEmail
            customerFirstName
            customerLastName
            shippingMethod
            couponCode
            isGift
            itemsCount
            itemsQty
        }
        jumpToSection
    }
}

query advertisements {
    advertisements {
        advertisementFour
        advertisementThree
        advertisementTwo
        cart {
            id
            customerEmail
            customerFirstName
            customerLastName
            shippingMethod
            couponCode
            isGift
            itemsCount
            itemsQty
        }
    }
}

query notificationList {
    notificationList(
        first: 10,
        page: 1,
    ) {
        paginatorInfo {
            count
            currentPage
            lastPage
            total
        }
        data {
            id
            image
            imageUrl
            type
            productCategoryId
            status
            createdAt
            updatedAt
            translations {
                id
                title
                content
                locale
                channel
                pushNotificationId
            }
        }
    }
}

mutation sendNotification {
    sendNotification(input: {
        id: 1
    }) {
        success
        status
        messageId
    }
}
