graphql.query:
query headerContents {
    headerContents {
        paginatorInfo {
            count
            currentPage
            lastPage
            total
        }
        data {
            id
            contentType
            position
            status
            translations {
                id
                contentId
                title
                customTitle
                customHeading
                pageLink
                linkTarget
                catalogType
                products
                description
                locale
                createdAt
                updatedAt
            }
        }
    }
}
